import React, { Component } from 'react';
// import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import video_mp4 from '../../../img/vid/phl-chat.mp4';
import video_webm from '../../../img/vid/phl-chat.webm';

import data from "./project_data.json";

function Project() {

    return (

      <div>
        {data.map((item) => (
          <article className={item.id}>
              <div className="project-content">
                <span>{item.date}</span>
                <h2>{item.title}</h2>
                <p>{item.paragraph_01}</p>
                <p>{item.paragraph_02}</p>
                <a href={item.link} className="button" target="_blank" rel="noreferrer">Learn more</a>
              </div>
              <div className="project-visual" >
                <video autoplay="autoplay" muted="muted" playsinline="playsinline" preload="auto" loop="loop" className="project-visual_vid" onclick="this.play();">
                  <source src={video_mp4} type="video/mp4"/>
                  <source src={video_webm} type="video/mp4"/>
                </video>
              </div>
          </article>
        ))}
      </div>

    );
  }

export default Project;
